// Graph.js
import React from 'react';
import { Line } from 'react-chartjs-2';
import { Chart, registerables } from 'chart.js';
Chart.register(...registerables);

const Graph = ({ func, a, b, n = 10 }) => {
  // Крок розбиття для методу прямокутників
  const h = (b - a) / n;

  // Значення x і y для графіка функції
  const xValues = Array.from({ length: 1000 }, (_, i) => a + (i * (b - a)) / 999);
  const yValues = xValues.map(func);

  // Прямокутники для методу прямокутників
  const rectangleX = Array.from({ length: n }, (_, i) => a + i * h);
  const rectangleY = rectangleX.map(func);

  const data = {
    labels: xValues,
    datasets: [
      {
        label: 'f(x) = x^2 * cos(x)',
        data: yValues,
        fill: false,
        borderColor: 'blue',
        pointRadius: 0,
        borderWidth: 2,
      },
      {
        label: 'Rectangle Approximation',
        type: 'bar',
        data: rectangleY,
        backgroundColor: 'rgba(255, 0, 0, 0.5)', // Червоні прямокутники
        barThickness: h * 1000 / (b - a), // Динамічний розмір для видимості
      },
    ],
  };

  const options = {
    responsive: true,
    maintainAspectRatio: false,
    scales: {
      x: {
        title: { display: true, text: 'x' },
        type: 'linear',
        position: 'bottom',
        min: a,
        max: b,
      },
      y: {
        title: { display: true, text: 'f(x)' },
      },
    },
  };

  return (
    <div style={{ width: '600px', height: '400px' }}>
      <Line data={data} options={options} />
    </div>
  );
};

export default Graph;
