// Graph.js
import React from 'react';
import { Line } from 'react-chartjs-2';
import { Chart, registerables } from 'chart.js';
Chart.register(...registerables);

const Graph = ({ func, a, b }) => {
  // Генеруємо значення x і y для графіка
  const xValues = Array.from({ length: 1000 }, (_, i) => a + (i * (b - a)) / 999);
  const yValues = xValues.map(func);

  const data = {
    labels: xValues,
    datasets: [
      {
        label: 'f(x) = x^2 * cos(x)',
        data: yValues,
        fill: true,
        backgroundColor: 'rgba(255, 0, 0, 0.5)', // Світло-блакитний
        borderColor: 'pink',
      },
    ],
  };

  const options = {
    responsive: true,
    maintainAspectRatio: false, // Вимикаємо співвідношення для налаштування розмірів
    scales: {
      x: { title: { display: true, text: 'x' } },
      y: { title: { display: true, text: 'f(x)' } },
    },
  };

  return (
    <div style={{ width: '1300px', height: '300px' }}> {/* Задаємо розмір контейнера */}
      <Line data={data} options={options} />
    </div>
  );
};

export default Graph;
