export function integrateRectangleMethod(func, a, b, n) {
  const h = (b - a) / n;
  let sum = 0;

  for (let i = 0; i < n; i++) {
    const x = a + i * h;
    sum += func(x);
  }

  return sum * h;
}

export const func = (x) => Math.pow(x, 2) * Math.cos(x);
export const intervals = [10, 20, 50, 100, 1000];

