import { useState } from "react";
import {
  integrateRectangleMethod,
  integrateTrapezoidalMethod,
  integrateMonteCarloMethod,
  func2,
  intervals,
  analyticValue2,
} from "../utils/methods";
import styles from "../styles/Example2.module.css"; 


const Example2 = () => {
  const [results, setResults] = useState([]);
  const [a, setA] = useState(4);
  const [b, setB] = useState(3.2);
  const [selectedFunc, setSelectedFunc] = useState("func");



  const calculateIntegrals = () => {
    // Choose the function and analytic value based on selection
    const activeFunc = selectedFunc === "func" ? func : func2;
    const analytic = selectedFunc === "func" ? analyticValue : analyticValue2;

    const calculations = intervals.map((n) => {
      const rectangleResult = integrateRectangleMethod(activeFunc, a, b, n);
      const trapezoidalResult = integrateTrapezoidalMethod(activeFunc, a, b, n);
      const monteCarloResult = integrateMonteCarloMethod(activeFunc, a, b, n);
      return {
        n,
        analyticValue: analytic,
        rectangleMethod: rectangleResult,
        trapezoidalMethod: trapezoidalResult,
        monteCarloMethod: monteCarloResult,
      };
    });
    setResults(calculations);
  };

  return (
    <div className={styles.container}>


      <p className={styles.subtitle}>
        Обчислити визначений інтеграл <strong>∫ 1/sqrt(0.5x + 1) dx</strong>
      </p>



      <div className={styles.inputGroup}>
        <label className={styles.label}>
          Нижня межа (a):
          <input
            type="number"
            value={a}
            onChange={(e) => setA(parseFloat(e.target.value))}
          />
        </label>
        <label className={styles.label}>
          Верхня межа (b):
          <input
            type="number"
            value={b}
            onChange={(e) => setB(parseFloat(e.target.value))}
          />
        </label>
        <button onClick={calculateIntegrals} className={styles.button}>Обчислити</button> 
      </div>



      {results.length > 0 && (
        <table className={styles.table}>
          <thead>
            <tr>
              <th>N</th>
              <th>Аналітичне значення</th>
              <th>Метод прямокутників</th>
              <th>Метод трапецій</th>
              <th>Метод Монте-Карло</th>
            </tr>
          </thead>
          <tbody>
            {results.map((row) => (
              <tr key={row.n}>
                <td>{row.n}</td>
                <td>{row.analyticValue.toFixed(6)}</td>
                <td>{row.rectangleMethod.toFixed(6)}</td>
                <td>{row.trapezoidalMethod.toFixed(6)}</td>
                <td>{row.monteCarloMethod.toFixed(6)}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}

    </div>
  );
};

export default Example2;
